import axios from "axios";
const API_URL ="http://localhost:8080/users/";


const getuser = (usersId) => {
    return axios.get(`${API_URL}${usersId}`);
}
const login = (users) => {
    return axios.post(`${API_URL}`,users);
}

const usersService = {
    getuser,
    login,
};


export default usersService;
